(1076, 17, 1, 'What does \"cultural diversity\" mean for Nigeria\'s identity?', 'Nigeria has only one main culture.', 'Nigeria has no culture.', 'Nigeria is a very small country.', 'Nigeria has a rich variety of different cultures, ethnic groups, and traditions.', NULL, NULL, NULL, NULL, NULL, NULL, '4', 0, '2025-07-01 10:25:54'),
(1077, 17, 1, 'What is a \"cultural practice\"?', 'A new invention.', 'A common way of doing things, celebrating, or believing within a specific culture.', 'A rule that is never followed.', 'A type of animal.', NULL, NULL, NULL, NULL, NULL, NULL, '2', 0, '2025-07-01 10:25:54'),
